import React, { useEffect, useState,  } from 'react';
import { StatusBar } from 'expo-status-bar';
import { Text, View, StyleSheet, FlatList, TouchableOpacity, Alert } from 'react-native';
import Api from './Api';




export default function Frutas() {

const [nomeFruta,setNomeFruta] = useState('');

useEffect(()=>{
  Api.get(`/hortfrut`).then(response => {
    setNomeFruta(response.data);
  });
},[]);

function getFrutas(){
  Api.get(`/hortfrut`).then(response =>{
    setNomeFruta(response.data[0]);
  }).cath(error =>{
    Alert.alert('Atenção','Frutas não localizadas')
  })
}
  return (
    <View style={estilo.container}>

      <Text style={estilo.titulo}>Lista de Frutas</Text>

      <FlatList
        data={frutas}
        renderItem={({ item }) =>
          <TouchableOpacity>
            <View style={estilo.grupoFrutas}>

              <Text style={estilo.textoBotaoFruta}>{item.fruta}</Text>
              <Text style={estilo.textoBotaoValor}>{item.valor}</Text>

            </View>
          </TouchableOpacity>

        }
      />

      <StatusBar style="auto" />
    </View>
  );
}


const estilo = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#e9c46a',

  },
  titulo: {
    fontSize: 30,
    color: "#2a9d8f",
    textAlign: 'center',
    justifyContent: 'center',
    marginVertical: 20
  },
  grupoFrutas: {
    backgroundColor: '#2a9d8f',
    margin: 15,
    padding: 5,
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  textoBotaoFruta: {
    fontSize: 20
  },
  textoBotaoValor: {
    fontSize: 20
  }


});
